mapper.select:
select bul.log_no, bul.lease_id, bul.log_type, bum.user_no, if(content is null, '', content) as content, bum.user_id, bum.email, bum.status, bui.name, bui.phone, date_format(bul.created_at, '%Y년%m월%d %H:%i') as created_at from (select bul.*, lm.lease_id from (select * from leases where lease_id = #{lease_id}) lm join bike_user_log bul on lm.lease_no = bul.ref_id where 1 = 1
and log_no < #{next_token}
order by bul.log_no desc limit 30) bul join bike_user_mst bum on bul.from_user_no = bum.user_no join bike_user_info bui on bum.user_no = bui.user_no

select count(*)
  from bike_user_todo
 where to_user_no = #{sess_user_no}
