mapper.select:
select dl.demand_lease_id, dl.demand_lease_status, dl.car_model as car_model_code, cb.comm_nm as car_model, dl.color, dl.management_type, dl.period, dl.expire_type, dl.pre_payment, dl.payment_type, dl.is_maintenance, dl.extra_info, dl.created_at, dl.pending_at, dl.rejected_at, dl.completed_at, c.client_id, c.business_type as client_business_type, c.email as client_email, c.reg_no as client_reg_no, ci.name as client_name, ci.phone as client_phone, ci.manager_name as client_manager_name, l.lease_id from (select * from demand_leases where demand_lease_status != '0'
and demand_lease_id < #{next_token}
order by demand_lease_id desc limit 40) dl join clients c on dl.client_no = c.client_no join client_info ci on c.client_no = ci.client_no join com_comm_bikes cb on dl.car_model = cb.comm_cd left outer join leases l on dl.lease_no = l.lease_no order by demand_lease_id desc

select count(*) from inquiries where 1 = 1
and status = #{search_status}
and company like concat('%', #{search_company}, '%')

select cast(inquiry_no as char) as inquiry_no, iq.status, iq.business_type, iq.email as inquiry_email, iq.phone as inquiry_phone, iq.company as inquiry_company, iq.content, iq.name, iq.created_at, um.user_id, um.email as user_email, ui.name as username, ui.phone as user_phone, iq.confirmed_at from (select * from inquiries where 1 = 1
and inquiry_no < #{next_token}
and status = #{search_status}
and company like concat('%', #{search_company}, '%')
order by inquiry_no desc limit 50) iq left outer join bike_user_mst um on iq.confirmed_user_no = um.user_no left outer join bike_user_info ui on um.user_no = ui.user_no order by inquiry_no desc
